import java.util.HashMap;
import java.util.Map;

public class EncryptString2 {
    public static void main(String[] args){
        String S = "";
        HashMap<Integer,Character> hexa=new HashMap<>();
        hexa.put(10,'a');
        hexa.put(11,'b');
        hexa.put(12,'c');
        hexa.put(13,'d');
        hexa.put(14,'e');
        hexa.put(15,'f');
        HashMap<Character,Integer> freq=new HashMap<>();
        for (int i = 0; i < S.length(); i++) {
            char ch=S.charAt(i);
            if(freq.containsKey(ch)) freq.put(ch,freq.get(ch)+1);
            else freq.put(ch,1);
        }
        System.out.println(freq);
        String res="",rev="";
        for (int i = 0; i < S.length(); i++) {
            char ch=S.charAt(i);
            if(hexa.containsKey(freq.get(ch))&&(!(res.contains(ch+"")))){
                for(Map.Entry<Integer,Character> entry: hexa.entrySet()){
                    if((entry.getKey()==freq.get(ch))&&(!res.contains(ch+""))){
                        res+=ch;
                        res+=entry.getValue();
                    }
                }
            }
            else if((!(hexa.containsKey(freq.get(ch))))&&(!res.contains(ch+""))){
                for(Map.Entry<Character,Integer> entry:freq.entrySet()){
                    if(ch== entry.getKey()){
                        res+=entry.getKey();
                        res+=entry.getValue();
                    }
                }
            }
        }
        System.out.println(res);
        for (int i = res.length()-1; i >= 0; i--) {
            char ch=res.charAt(i);
            rev+=ch;
        }
        System.out.println(rev);
    }
}
